#ifndef SRSGNB_F1_INTERFACE_F1U_H
#define SRSGNB_F1_INTERFACE_F1U_H

#include "srsgnb/ADT/byte_buffer.h"

namespace srsgnb {

/// Packet entry point for the F1u interface.
class F1u_input_gateway
{
public:
  virtual ~F1u_input_gateway() = default;

  /// Handles the input packet.
  virtual void handle(byte_buffer& data) = 0;
};

/// Packet notification interface.
class F1u_packet_notifier
{
public:
  virtual ~F1u_packet_notifier() = default;

  /// This callback is invoked for each outgoing packed that is generated by the F1 interface.
  virtual void on_new_packet(byte_buffer& data) = 0;
};

} // namespace srsgnb

#endif // SRSGNB_F1_INTERFACE_F1U_H
