#ifndef SRSGNB_PDCP_PDCP_H
#define SRSGNB_PDCP_PDCP_H

#include "srsgnb/ADT/byte_buffer.h"

namespace srsgnb
{

/// Packet entry point for the PDCP interface.
class pdcp_input_gateway
{
public:
  virtual ~pdcp_input_gateway() = default;

  /// Handles the input packet.
  virtual void handle(byte_buffer& data) = 0;
};

/// Packet notification interface.
class pdcp_packet_notifier
{
public:
  virtual ~pdcp_packet_notifier() = default;

  /// This callback is invoked for each outgoing packed that is generated by the PDCP interface.
  virtual void on_new_packet(byte_buffer& data) = 0;
};

}

#endif // SRSGNB_PDCP_PDCP_H
