#ifndef SRSGNB_SDAP_SDAP_H
#define SRSGNB_SDAP_SDAP_H

#include "srsgnb/adt/byte_buffer.h"

namespace srsgnb {

/// Packet entry point for the SDAP interface.
class sdap_input_gateway
{
public:
  virtual ~sdap_input_gateway() = default;

  /// Handles the input packet.
  virtual void handle(byte_buffer& data) = 0;
};

/// Packet notification interface.
class sdap_packet_notifier
{
public:
  virtual ~sdap_packet_notifier() = default;

  /// This callback is invoked for each outgoing packed that is generated by the SDAP interface.
  virtual void on_new_packet(byte_buffer& data) = 0;
};

} // namespace srsgnb

#endif // SRSGNB_SDAP_SDAP_H
